/**
 * Function to apply merge revert styles for a theme
 * @param styles Styles for the merge revert buttons
 * @param styles.backgroundColor Background color of the revert area
 * @param styles.borderColor Border color of the revert area
 * @param styles.buttonColor Color of the revert buttons
 * @param styles.buttonHoverColor Hover color of the revert buttons
 */
function applyMergeRevertStyles(styles) {
    // Create a stylesheet
    const styleEl = document.createElement('style');
    styleEl.id = 'cm-merge-revert-styles';
    // Define CSS with the theme-specific values
    styleEl.textContent = `
    .cm-merge-revert {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: flex-start;
      padding: 4px;
      background-color: ${styles.backgroundColor};
      border-left: 1px solid ${styles.borderColor};
      border-right: 1px solid ${styles.borderColor};
      width: 32px;
    }
    
    .cm-merge-revert button {
      width: 100%;
      height: auto;
      background-color: transparent;
      border: none;
      color: ${styles.buttonColor};
      cursor: pointer;
      margin: 0 auto;
      font-size: 20px;
    }
    
    .cm-merge-revert button:hover {
      background-color: ${styles.buttonHoverColor};
    }
  `;
    // Remove any existing merge styles
    const existingStyle = document.getElementById('cm-merge-revert-styles');
    if (existingStyle)
        existingStyle.remove();
    // Add the new styles
    document.head.appendChild(styleEl);
}

// Enhanced Abcdef theme colors with improved contrast and harmony
const base00 = '#0a0e14', // Background (slightly deeper for better contrast)
base01 = '#e0edff', // Foreground (slightly bluer tint for better readability)
base02 = '#3a4a5f', // Selection (richer blue tone)
base03 = '#242936', // Gutter background (distinguished from selection)
base04 = '#d8e1f0', // Gutter foreground (softer white)
base05 = '#4fc1ff', // Caret (bright cyan blue for visibility)
base06 = '#3a4a5f40', // Line highlight with balanced opacity
base07 = '#1a2030', // Dark background for panels
base08 = '#efbb24', // Keyword (warm gold instead of darkgoldenrod)
base09 = '#7799ff', // Atom (brighter blue)
base0A = '#8c8f93', // Comment (slightly bluer gray)
base0B = '#c792ea', // Number (softer purple)
base0C = '#ffee99', // Definition, Function (softer yellow)
base0D = '#abcdef', // Variable (keeping the theme's signature color)
base0E = '#ffcc44', // Type Name (warmer yellow)
base0F = '#99c2ff', // Tag Name (light blue)
// UI elements
invalid = '#ff5370', // Invalid color (more visible red)
darkBackground = base07, highlightBackground = base06, tooltipBackground = '#0f1521', // Darker tooltip for better contrast
cursor = base05, selection = base02, selectionForeground = '#ffffff', // Selection text color
activeBracketBg = '#3a4a5f60', // Active bracket background
activeBracketBorder = '#abcdef', // Active bracket border (signature color);
// Diff/merge specific colors
addedBackground = '#1d391d4d', // Dark green for insertions
removedBackground = '#391d1d08', // Dark red for deletions
addedText = '#7aecb3', // Bright mint green for added text
removedText = '#ff8a80'; // Soft red for removed text
