/**
 * Enhanced syntax highlighting for Abcdef theme
 */
const abcdefHighlightStyle = language.HighlightStyle.define([
    // Keywords and control flow
    { tag: highlight.tags.keyword, color: base08, fontWeight: 'bold' },
    { tag: highlight.tags.controlKeyword, color: base08, fontWeight: 'bold' },
    { tag: highlight.tags.moduleKeyword, color: base08, fontWeight: 'bold' },
    // Names and variables
    { tag: [highlight.tags.name, highlight.tags.deleted, highlight.tags.character, highlight.tags.macroName], color: base09 },
    { tag: [highlight.tags.variableName], color: base09 },
    { tag: [highlight.tags.propertyName], color: base0D, fontStyle: 'normal' },
    // Classes and types
    { tag: highlight.tags.typeName, color: base0E },
    { tag: highlight.tags.className, color: base0D, fontStyle: 'italic' },
    { tag: highlight.tags.namespace, color: '#78a0d3', opacity: 0.8 },
    // Operators and punctuation - clearer blues
    { tag: [highlight.tags.operator, highlight.tags.operatorKeyword], color: '#ff9cac' },
    { tag: [highlight.tags.bracket], color: '#d0d6e0' },
    { tag: [highlight.tags.brace], color: '#d0d6e0' },
    { tag: [highlight.tags.punctuation], color: '#d0d6e0' },
    // Functions and parameters
    { tag: highlight.tags.function(highlight.tags.variableName), color: base0C },
    { tag: highlight.tags.definition(highlight.tags.variableName), color: base0C },
    // Constants and literals
    { tag: highlight.tags.number, color: base0B },
    { tag: highlight.tags.changed, color: '#ff9cac' },
    { tag: highlight.tags.annotation, color: '#ffad5c', fontStyle: 'italic' },
    { tag: highlight.tags.modifier, color: '#ffad5c', fontStyle: 'italic' },
    { tag: highlight.tags.self, color: base0B },
    { tag: [highlight.tags.color, highlight.tags.constant(highlight.tags.name), highlight.tags.standard(highlight.tags.name)], color: base0B },
    { tag: [highlight.tags.atom, highlight.tags.bool, highlight.tags.special(highlight.tags.variableName)], color: base0B },
    // Strings and regex
    { tag: [highlight.tags.processingInstruction, highlight.tags.inserted], color: '#7aecb3' },
    { tag: [highlight.tags.special(highlight.tags.string), highlight.tags.regexp], color: '#6acdbe' /* Turquoise */ },
    // Punctuation and structure
    { tag: highlight.tags.definition(highlight.tags.typeName), color: base0E, fontWeight: 'bold' },
    { tag: [highlight.tags.operator, highlight.tags.operatorKeyword], color: '#ff9cac' },
    { tag: highlight.tags.bracket, color: '#d0d6e0' },
    { tag: [highlight.tags.brace], color: '#d0d6e0' },
    { tag: highlight.tags.punctuation, color: '#d0d6e0' },
    // Comments and documentation
    { tag: highlight.tags.meta, color: '#78a0d3' },
    { tag: highlight.tags.comment, fontStyle: 'italic', color: base0A },
    { tag: highlight.tags.docComment, fontStyle: 'italic', color: base0A },
    // HTML/XML elements
    { tag: highlight.tags.tagName, color: base0F },
    { tag: highlight.tags.attributeName, color: '#ffad5c' },
    // Markdown and text formatting
    { tag: highlight.tags.heading, color: base0E, fontWeight: 'bold' },
    { tag: [highlight.tags.strong], fontWeight: 'bold' },
    { tag: [highlight.tags.emphasis], fontStyle: 'italic' },
    // Links and URLs
    { tag: highlight.tags.link, color: '#ffcc44', fontWeight: '500' },
    {
        tag: highlight.tags.url,
        color: '#66c2ff',
        textDecoration: 'underline',
        textUnderlineOffset: '2px',
    },
    // Special states
    { tag: highlight.tags.invalid, color: invalid, textDecoration: 'underline wavy' },
    { tag: highlight.tags.strikethrough, color: invalid, textDecoration: 'line-through' },
    // Enhanced syntax highlighting
    { tag: highlight.tags.constant(highlight.tags.name), color: base0B },
    { tag: highlight.tags.controlKeyword, color: base08, fontWeight: 'bold' },
    { tag: highlight.tags.deleted, color: invalid },
    { tag: highlight.tags.labelName, color: '#ffad5c' },
    { tag: highlight.tags.string, color: '#7aecb3' /* New mint green for strings */ },
]);
/**
 * Combined Abcdef theme extension
 */
const abcdef = [
    abcdefTheme,
    language.syntaxHighlighting(abcdefHighlightStyle),
];
/**
 * Abcdef merge revert styles configuration
 */
const abcdefMergeStyles = {
    backgroundColor: base03, // Using theme-specific color variables
    borderColor: base02,
    buttonColor: selectionForeground,
    buttonHoverColor: base02,
};

exports.abcdef = abcdef;
exports.abcdefMergeStyles = abcdefMergeStyles;
exports.applyMergeRevertStyles = applyMergeRevertStyles;
